computed.playerLabels:
const labels = []

if (playerLoginDetails.value.isUserTurn) {
  labels.push('Your Turn')
}

if (!playerLoginDetails.value.isUserTurn && turnUserId.value !== null) {
  labels.push('Other Player Turn')
}

if (playerLoginDetails.value.isDealer) {
  labels.push('Dealer')
}

if (playerLoginDetails.value.isSmallBlind) {
  labels.push('Small Blind')
}

if (playerLoginDetails.value.isBigBlind) {
  labels.push('Big Blind')
}

return labels.join(' / ')
